import React, { useState } from 'react';
import { MapContainer as LeafletMap, TileLayer, Marker, Popup, useMapEvents } from "react-leaflet";
import "leaflet/dist/leaflet.css";
import L from 'leaflet';


function LocationMarkers({ values, handleChange, handleWhLatLonChange }) {

  const [marker, setMarker] = useState([values.wh_latlon[0], values.wh_latlon[1]]);
  const defaultMarker = 'data:image/png;base64,';
  
  var defaultIcon = new L.Icon({
      iconUrl: defaultMarker,
      iconAnchor: [12, 41],
  });

  const map = useMapEvents({
    click(e) {
      handleWhLatLonChange(e)
      setMarker(e.latlng)
    }
  });

  return (
    <React.Fragment>
      <Marker 
        position={marker} 
        icon={defaultIcon}
        key={1}
    ></Marker>)
    </React.Fragment>
  );
}
  
function RecommendationsMap({ values, handleChange, handleWhLatLonChange }) {
    const mapCentre = [values.wh_latlon[0], values.wh_latlon[1]]

    return (
        <LeafletMap 
          center={mapCentre}
          style={{height: "40vh", width: "700px"}} 
          zoom={13}
        >
        <TileLayer
            url="https://api.mapbox.com/styles/v1/seedovan/cl0cs50x7001s14taxgl4jj9t/tiles/256/{z}/{x}/{y}@2x?access_token=pk.eyJ1Ijoic2VlZG92YW4iLCJhIjoiY2wwY3M5MDJtMDI4aTNjcDkyem9hdnI4YiJ9.G7t_vQ3A7RlXG6zMx__-mg"
            attribution="Map data &copy; <a href=&quot;https://www.openstreetmap.org/&quot;>OpenStreetMap</a> contributors, <a href=&quot;https://creativecommons.org/licenses/by-sa/2.0/&quot;>CC-BY-SA</a>, Imagery &copy; <a href=&quot;https://www.mapbox.com/&quot;>Mapbox</a>"
        />
        <LocationMarkers 
          values = {values}
          handleChange = {handleChange}
          handleWhLatLonChange = { handleWhLatLonChange }
        />
        </LeafletMap>
    );
}

export default RecommendationsMap;

